import React, { useEffect, useState } from "react";
import SearchFilter from "./SearchFilter";
import ItemCard from "./ItemCard";
import indexImage1 from '../assets/index1.jpg';
import { useDispatch } from 'react-redux';
import { fetchProductsSuccess } from '../redux/actions';

const DIsplayItem = () => {
    const dispatch = useDispatch();
    const [selectedComponent, setSelectedComponent] = useState(null);
    const [data, setData] = useState({
        "sofa": [
            {
                "id": 1,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/be/61/0b/be610b47a66e0a9cc5d9599796675681.jpg",
                "name": "Sofa Model 1",
                "desc": "Solimo Tulip Leatherette 3 Seater Sofa",
                "price": 17000,
                "count": 6
            },
            {
                "id": 2,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/40/78/20/4078202662d91e6e5496d3f650572a69.jpg",
                "name": "Sofa Model 2",
                "desc": "Home Centre Emily Fabric 3 Seater Polyester Sofa",
                "price": 15999,
                "count": 10
            },
            {
                "id": 3,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/67/f6/bc/67f6bc4ae2d912d7d8cd27ddbcd0be1d.jpg",
                "name": "Sofa Model 3",
                "desc": "Coirfit Three Seater Folding Sofa",
                "price": 8498,
                "count": 5
            },
            {
                "id": 4,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/3d/26/9e/3d269eff814e95b73a76939cbff28836.jpg",
                "name": "Sofa Model 4",
                "desc": "Solimo Tulip Leatherette Sofa for Living Room",
                "price": 17999,
                "count": 12
            },
            {
                "id": 5,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/a2/e2/77/a2e277507667edd2b14f77b656c90530.jpg",
                "name": "Sofa Model 5",
                "desc": "Wakefit Sofa Set for Living Room",
                "price": 8885,
                "count": 4
            },
            {
                "id": 6,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/ef/1c/e6/ef1ce6491e49bda8cea77254d38fb378.jpg",
                "name": "Sofa Model 6",
                "desc": "Seventh Heaven Milan 3 Seater Sofa, Chenille Molfino Fabric",
                "price": 12999,
                "count": 6
            },
            {
                "id": 7,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/d5/ab/2f/d5ab2fdb7ce08d7a5c5f8406e925689c.jpg",
                "name": "Sofa Model 7",
                "desc": "Solimo Rachel 3 Seater Sectional Sofa",
                "price": 10800,
                "count": 3
            },
            {
                "id": 8,
                "category": "sofa",
                "imageUrl": "https://i.pinimg.com/564x/64/51/c5/6451c5bc7c699540f7a67c784f78a69d.jpg",
                "name": "Sofa Model 8",
                "desc": "Casaliving Minta LHS 6 Seater L Shape Sofa Set for Living Room",
                "price": 18353,
                "count": 7
            },
        ],
        "bed": [
            {
                "id": 1,
                "category": "bed",
                "imageUrl": "https://i.pinimg.com/564x/64/51/c5/6451c5bc7c699540f7a67c784f78a69d.jpg",
                "name": "Bed Model 1",
                "desc": "Casaliving Minta LHS 6 Seater L Shape Sofa Set for Living Room",
                "price": 18353,
                "count": 7
            },
            {
                "id": 2,
                "category": "bed",
                "imageUrl": "https://i.pinimg.com/564x/64/51/c5/6451c5bc7c699540f7a67c784f78a69d.jpg",
                "name": "Bed Model 2",
                "desc": "Casaliving Minta LHS 6 Seater L Shape Sofa Set for Living Room",
                "price": 18353,
                "count": 5
            },
            {
                "id": 3,
                "category": "bed",
                "imageUrl": "https://i.pinimg.com/564x/64/51/c5/6451c5bc7c699540f7a67c784f78a69d.jpg",
                "name": "Bed Model 3",
                "desc": "Casaliving Minta LHS 6 Seater L Shape Sofa Set for Living Room",
                "price": 18353,
                "count": 15
            }
        ]
    });

    const openModal = (selected) => {
        setSelectedComponent(selected);
        console.log(selectedComponent);
    };

    const closeModal = () => {
        setSelectedComponent(null);
        console.log("clicked         **", selectedComponent);
    };

    // const fetchSofaData = () => { 

    // };

    useEffect(() => {
        // Axios.get('/api/products')
        //     .then((response) => {
        //         const productData = response.data;
        //         console.log("productData", productData);
        //         // Use the productData in your React components
        //     })
        //     .catch((error) => {
        //         console.error('Error fetching product data:', error);
        //     });
        dispatch(fetchProductsSuccess(data));
    }, [dispatch, data])

    return (
        <>
            {selectedComponent ? (
                <ItemCard selectedComponent={selectedComponent} closeSelected={closeModal} />
            ) : (
                <div className="px-32 py-8">
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 rounded-full">
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index1" onClick={() => openModal("bed")} />
                            <h6>Bed</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index2" onClick={() => openModal("sofa")} />
                            <h6>Sofa</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index3" onClick={() => openModal("dining-table")} />
                            <h6>Dining Table</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index4" onClick={() => openModal("pendant-lights")} />
                            <h6>Pendant Lights</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index5" onClick={() => openModal("curtains")} />
                            <h6>Curtains</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index6" onClick={() => openModal("bean-bag")} />
                            <h6>Bean bag</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index7" onClick={() => openModal("swings")} />
                            <h6>Swings</h6>
                        </div>
                        <div className="flex flex-col justify-center items-center">
                            <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={indexImage1} alt="index8" onClick={() => openModal("kitchen-set")} />
                            <h6>Kitchen set</h6>
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default DIsplayItem;
