import React, { useState } from 'react';
import SearchIcon from '@mui/icons-material/Search';

const SearchFilter = ({ setFilterActive }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const handleSearch = () => {
    setFilterActive(true);
    onSearch(searchQuery);
  };

  const handleFilter = () => {
    onFilter({
      category: selectedCategory,
      minPrice: parseFloat(minPrice) || 0,
      maxPrice: parseFloat(maxPrice) || Infinity,
    });
  };

  return (
    <div className='bg-stone-600 px-2 py-2 flex justify-between text-white'>
      <div className='bg-stone-300 border-black rounded-r-lg rounded-l-lg h-7'>
        <input
          type="text"
          className='rounded-l-lg h-7 bg-stone-300 text-white'
          placeholder="Search by name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}><SearchIcon/></button>
      </div>
      <div>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="all">All Categories</option>
          <option value="sofa">Sofa</option>
          <option value="bed">Bed</option>
          {/* Add other categories as needed */}
        </select>
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button onClick={handleFilter}>Filter</button>
      </div>
    </div>
  );
};

export default SearchFilter;
