import React from 'react';
import CloseIcon from '@mui/icons-material/Close';

const ItemModal = ({ isOpen, onClose, addProduct }) => {
  console.log("addProduct", addProduct);
  return isOpen ? (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="modal-overlay absolute inset-0 bg-gray-900 opacity-50"></div>
      <div className="modal-content bg-white w-1/2 p-4 rounded shadow-lg text-center">
      <div className='w-8 h-8 rounded-full text-center bg-blue-400 right'><CloseIcon/></div>
        <h3 className='font-bold text-2xl pb-2'>{addProduct.name}</h3>
        <p>{addProduct.desc}</p>
        <p>Price: ${addProduct.price}/-</p>
        <p>Available: {addProduct.count}</p>
        <button
          onClick={onClose}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 my-4 px-4 rounded items-center"
        >
          Book Now
        </button>
      </div>
    </div>
  ) : null;

};

export default ItemModal;
