import React, { useState } from 'react';
import SearchIcon from '@mui/icons-material/Search';

const SearchFilter = ({ onSearch, onFilter }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const handleSearch = () => {
    onSearch(searchQuery);
  };

  const handleFilter = () => {
    onFilter({
      category: selectedCategory,
      minPrice: parseFloat(minPrice) || 0, // Convert to float or default to 0
      maxPrice: parseFloat(maxPrice) || Infinity, // Convert to float or default to Infinity
    });
  };

  return (
    <div className='bg-stone-600 px-2 py-2'>
      <div>
        <input
          type="text"
          className='border-black rounded-l-lg'
          placeholder="Search by name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button className='bbg-white order-black rounded-r-lg' onClick={handleSearch}><SearchIcon/></button>
      </div>
      <div>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="all">All Categories</option>
          <option value="sofa">Sofa</option>
          <option value="bed">Bed</option>
          {/* Add other categories as needed */}
        </select>
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button onClick={handleFilter}>Filter</button>
      </div>
    </div>
  );
};

export default SearchFilter;
