import React, { useState } from "react";
import ItemCard from "./ItemCard";

const DIsplayItem = () => {
    // const [selectedComponent, setSelectedComponent] = useState('0');

    const openModal = (component) => {
        // setSelectedComponent(component);
    };

    const closeModal = () => {
        // setSelectedComponent('0');
        // console.log("clicked         **", selectedComponent);
    };

    return (
        <>
            {/* {selectedComponent !== '0' ? (
                <ItemCard selectedComponent={selectedComponent} closeSelected={closeModal}/>
                null
            ) : ( */}
            <div className="px-32 py-8">
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 rounded-full">
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Bed")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index1.jpg')} alt="index1" />
                        <h6>Bed</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Sofa")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index2.jpg')} alt="index2" />
                        <h6>Sofa</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Dining table")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index3.jpg')} alt="index3" />
                        <h6>Dining Table</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Pendant lights")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index4.jpg')} alt="index4" />
                        <h6>Pendant Lights</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Curtains")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index5.jpg')} alt="index5" />
                        <h6>Curtains</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Bean bag")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index6.jpg')} alt="index6" />
                        <h6>Bean bag</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Swings")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index7.jpg')} alt="index7" />
                        <h6>Swings</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center" onClick={openModal("Kitchen set")}>
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index8.jpg')} alt="index8" />
                        <h6>Kitchen set</h6>
                    </div>
                </div>
            </div>
            {/* )} */}
        </>
    )
}

export default DIsplayItem;
