// productReducer.js
import { FETCH_PRODUCTS_SUCCESS } from './actionTypes'; // Define your action type

const initialState = {
  products: {
    sofa: [],
    bed: [],
    // ... other product categories
  },
};

const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_PRODUCTS_SUCCESS:
      return {
        ...state,
        products: action.payload,
      };
      case DECREMENT_PRODUCT_COUNT:
        const { productId } = action.payload;
        return {
          ...state,
          products: {
            ...state.products,
            [productId.category]: state.products[productId.category].map((product) =>
              product.id === productId.id
                ? { ...product, count: product.count - 1 }
                : product
            ),
          },
        };
      case RESET_PRODUCT_COUNT:
        // const { productId, newCount } = action.payload;
        return {
          ...state,
          products: {
            ...state.products,
            [productId.category]: state.products[productId.category].map((product) =>
              product.id === productId.id ? { ...product, count: newCount } : product
            ),
          },
        };
    default:
      return state;
  }
};

export default productReducer;
