// Define action types
export const FETCH_SOFA_DATA = 'FETCH_SOFA_DATA';
export const FETCH_BED_DATA = 'FETCH_BED_DATA';

// Action creators for fetching data
export const fetchSofaData = () => {
  return async (dispatch) => {
    try {
      // Replace this with your API call or JSON file fetch logic
      const response = await fetch('/api/sofa'); // Adjust the URL or endpoint
      const data = await response.json();

      dispatch({
        type: FETCH_SOFA_DATA,
        payload: data,
      });
    } catch (error) {
      console.error('Error fetching sofa data:', error);
    }
  };
};

export const fetchBedData = () => {
  return async (dispatch) => {
    try {
      // Replace this with your API call or JSON file fetch logic
      const response = await fetch('/api/bed'); // Adjust the URL or endpoint
      const data = await response.json();

      dispatch({
        type: FETCH_BED_DATA,
        payload: data,
      });
    } catch (error) {
      console.error('Error fetching bed data:', error);
    }
  };
};
