import React, { useState } from "react";
import ItemCard from "./ItemCard";

const DIsplayItem = () => {
    const [selectedComponent, setSelectedComponent] = useState(null);

    const openModal = (selected) => {
        console.log(selected);
        // setSelectedComponent(selected);
        console.log(selectedComponent);
    };

    const closeModal = () => {
        // setSelectedComponent('0');
        // console.log("clicked         **", selectedComponent);
    };

    return (
        <>
            {/* {selectedComponent !== '0' ? (
                <ItemCard selectedComponent={selectedComponent} closeSelected={closeModal}/>
                null
            ) : ( */}
            <div className="px-32 py-8">
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 rounded-full">
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index1.jpg')} alt="index1" onClick={() => openModal("Bed")}/>
                        <h6>Bed</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index2.jpg')} alt="index2" onClick={() => openModal("Sofa")}/>
                        <h6>Sofa</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index3.jpg')} alt="index3" onClick={() => openModal("Dining table")} />
                        <h6>Dining Table</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index4.jpg')} alt="index4" onClick={() => openModal("Pendant lights")} />
                        <h6>Pendant Lights</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index5.jpg')} alt="index5" onClick={() => openModal("Curtains")} />
                        <h6>Curtains</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index6.jpg')} alt="index6" onClick={() => openModal("Bean bag")} />
                        <h6>Bean bag</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index7.jpg')} alt="index7" onClick={() => openModal("Swings")} />
                        <h6>Swings</h6>
                    </div>
                    <div className="flex flex-col justify-center items-center">
                        <img className="w-52 h-52 mb-3 rounded-full shadow-lg border border-transparent hover:border-amber-400 transition-transform hover:scale-105" src={require('../assets/index8.jpg')} alt="index8" onClick={() => openModal("Kitchen set")} />
                        <h6>Kitchen set</h6>
                    </div>
                </div>
            </div>
            {/* )} */}
        </>
    )
}

export default DIsplayItem;
