import { FETCH_SOFA_DATA, FETCH_BED_DATA } from './actions';

// Initial state for the product data
const initialState = {
  sofa: [],
  bed: [],
};

// Reducer function
const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_SOFA_DATA:
      return {
        ...state,
        sofa: action.payload,
      };
    case FETCH_BED_DATA:
      return {
        ...state,
        bed: action.payload,
      };
    default:
      return state;
  }
};

export default productReducer;
